"""Creating Alphabet from a data text{letter} within an 'F string' will return the value of the letter as a variable name. For example:    i = 'K'    {i} = 1 => K = 1 """import numpy as np                     import cv2import osdata = np.genfromtxt('OCRDataSet/OCRDataSet.txt')data_size = 20 # Number of samples per letterrangeMin = 1rangeMax = 50alphabet = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j', 'k', 'l', 'm',             'n', 'o', 'p', 'q', 'r', 's', 't', 'u', 'v', 'w', 'x', 'y', 'z',            'zero', 'one', 'two', 'three', 'four', 'five', 'six', 'seven',             'eight', 'nine']if (os.path.isdir('alphabet') == False):    os.mkdir('alphabet')for letter in alphabet:     # Check / Create Directory    exec(f'if (os.path.isdir("alphabet/{letter}") == False): os.mkdir("alphabet/{letter}")')     for i in range(20):     # For the 20 handwriten formats of a single letter        exec(f'{letter}{i} = data[rangeMin:rangeMax,:].astype(np.uint8)')        exec(f'{letter}{i} = cv2.resize({letter}{i}, (200,200), interpolation=cv2.INTER_AREA)')        rangeMin = rangeMin + 50        rangeMax = rangeMax + 50        number = str(i)                exec(f'cv2.imwrite("alphabet/" + letter + "/" + letter + number + ".jpg", {letter}{i})')# =============================================================================# END ALPHABET CREATOR# =============================================================================# Training Set = 0 - a11# Test Set = 13 - 19# cv2.imshow('', a680)# cv2.waitKey()# cv2.destroyAllWindows()